mapper.select:
SELECT payment.p_id, payment.m_code, payment.s_id, payment.p_price, payment.p_quantity, payment.p_cancel_target_id, payment.p_time, payment.p_canceled, play.p_id, play.p_name, schedule.s_time, member.m_id FROM payment, schedule, play, member
play.p_id = schedule.p_id AND schedule.s_id = payment.s_id AND member.m_code = payment.m_code
AND member.m_id = #{member}
AND play.p_name LIKE CONCAT('%', #{play}, '%')
AND (SELECT m_id FROM member as mem WHERE mem.m_code = play.m_code) = #{playAdmin}
AND payment.p_time >= #{from}
AND payment.p_time <= #{until}
AND payment.p_canceled = #{canceled}
AND schedule.s_time <= NOW()
ORDER BY payment.p_time
LIMIT #{start},
#{row}
10

SELECT p_canceled
FROM payment
WHERE p_id = #{p_id}

mapper.update:
UPDATE payment
SET p_canceled = 1
WHERE p_id = #{p_id}
