mapper.select:
SELECT actor.a_id, actor.a_name, actor.a_birth, actor.a_homepage, actor.a_picture
FROM prefer_actor, actor
WHERE actor.a_id=prefer_actor.a_id
AND prefer_actor.m_code=#{m_code}
LIMIT #{pageDTO.startNum}, #{pageDTO.perPage}

SELECT COUNT(*)
FROM prefer_actor
WHERE prefer_actor.m_code=#{m_code}

SELECT COUNT(*)
FROM prefer_actor
WHERE m_code=#{m_code}
AND a_id=#{a_id}
