mapper.update:
UPDATE question_answer
SET 
	q_deleted = '1'
WHERE
	q_id = #{q_id}
	OR q_target_id = #{q_id}

mapper.select:
SELECT p_id, p_name
FROM play
WHERE m_code = #{m_code};

mapper.update:
update question_answer
set 
	q_content = #{q_content}
where
	q_target_id = #{q_target_id}
    AND q_id != #{q_target_id};
